g-ins/1.10.7/integration/bootstrap/3/dataTables.bootstrap.js");$pageData->addJs(WEB_ROOT."lib/datatables/dataTables.responsive.js");$pageData->addJs(WEB_ROOT."lib/validator.js");$pageData->addJs(WEB_ROOT."lib/ckeditor/ckeditor.js");$pageData->addJs(WEB_ROOT."lib/ckeditor/adapters/jquery.js");$pageData->addJs(WEB_ROOT."js/admin.js");//load page content$pageData->content = include_once ($pageFile);// Ser the page title pattern$pageData->title .= ($pageData->title==="")? "Mobel Auto Service" : " - Mobel Auto Service";if (isset($descriere)) {    $pageData->metaDescription($descriere);}if (isset($author)) {    $pageData->metaAuthor($author);}if (isset($keywords) && is_array($keywords)) {    $pageData->metaKeyword($keywords);}// Load the template file$pageTemplate = include_once 'views/page.php';echo $pageTemplate;