<?php/* Table head names id, username, first_name, last_name, email, tip, status  *//* @var $allUsers PDOStatement */$userTable = "<table class='table table-striped table-bordered table-hover dataTables' id='allUsers'>                        <thead>                            <tr>                                <th>Nr. Crt</th>                                <th>Nume</th>                                <th>Prenume</th>                                <th>Nume utilizator</th>                                <th>Adresa de email</th>                                <th>Tip</th>                                <th>Status</th>";if ($sessionUser->tip <= 1) {$userTable .= "                 <th>Modifica</th>                                <th>Sterge</th>";}$userTable .= "             </tr>                        </thead>                        <tbody>";if (isset($allUsers)) {    $nr = 1;    while ($username = $allUsers->fetchObject()){        $userTable .= "<tr class='odd gradeX'>";        $userTable .= "<td>$nr</td>";        $userTable .= "<td>$username->last_name</td>";        $userTable .= "<td>$username->first_name</td>";        $userTable .= "<td class='center'>$username->username</td>";        $userTable .= "<td class='center'>$username->email</td>";        $userTable .= "<td class='center'>".$drepturi[$username->tip]."</td>";        $userTable .= "<td class='center'>".$status[$username->status]."</td>";    if ($sessionUser->tip <= 1) {        $userTable .= "<td class='center'><a href='".WEB_ROOT."users/modify-user/".$username->id."'><i class='fa fa-pencil-square-o'></i></a></td>";        $userTable .= "<td class='center'><a href='".WEB_ROOT."users/delete-user/".$username->id."' onclick=\"return confirm('Esti sigur ca vrei sa stergi user-ul ".$username->username."?')\"><i class='fa fa-trash-o'></i></a></td>";    }        $userTable .= "</tr>";        $nr++;    }}$userTable .= "</tbody>                    </table>";return "<div class='row'>    <div class='col-lg-12'>        <div class='panel panel-default'>            <div class='panel-heading'>                Toti utilizatori            </div>            <!-- /.panel-heading -->            <div class='panel-body'>                <div class='dataTable_wrapper'>                $userTable                </div>            </div>        </div>    </div></div>";